  document.addEventListener("DOMContentLoaded", function () {
    const logo = document.getElementById("floatingLogo");
    const footer = document.querySelector("footer");

function applyParallax() {
  if (!logo || !footer) return;

  const isMobile = window.innerWidth <= 768;
  const fadeInScrollY = isMobile ? 500 : 200;
  const fadeOutOffset = isMobile ? 300 : 200;

  const scrollY = window.scrollY;
  const footerTop = footer.getBoundingClientRect().top + window.scrollY;
  const visibleStart = fadeInScrollY;
  const visibleEnd = footerTop - fadeOutOffset;

  const visibleRange = visibleEnd - visibleStart;
  if (scrollY < visibleStart || scrollY > visibleEnd) {
    logo.style.transform = `translateY(0px)`;
    return;
  }

  const progress = (scrollY - visibleStart) / visibleRange;
  const easedProgress = (1 - Math.cos(progress * Math.PI)) / 2;

  const startY = 0;
  const endY = Math.min(visibleEnd - visibleStart, window.innerHeight - 400);
  const offset = startY + (endY - startY) * easedProgress;

  logo.style.transform = `translateY(${offset}px)`;
}





function checkPosition() {
  if (!footer || !logo) return;

  const isMobile = window.innerWidth <= 768;
  const fadeInScrollY = isMobile ? 500 : 200;
  const fadeOutOffset = isMobile ? 100 : 200;

  const scrollY = window.scrollY;
  const logoHeight = logo.offsetHeight;
  const logoTop = logo.getBoundingClientRect().top + window.scrollY;
  const logoBottomPosition = logoTop + logoHeight;
  const footerTop = footer.getBoundingClientRect().top + window.scrollY;

  if (scrollY < fadeInScrollY || logoBottomPosition + fadeOutOffset > footerTop) {
    logo.style.opacity = "0";
  } else {
    logo.style.opacity = "1";
  }
}

    function onScroll() {
      checkPosition();
      applyParallax();
    }

    window.addEventListener("scroll", onScroll);
    window.addEventListener("resize", checkPosition);
    checkPosition();
    applyParallax();
  });
